(1, 'Password Reset', '<p>\r\n	Dear Customer,<br />\r\n	You requested to change your password information at {$website_name}. Below is the URL where you can reset your password:</p>\r\n<table border="0" cellpadding="0" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="3">\r\n				<br />\r\n				<strong>Account information:</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					Email:</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<a href="mailto:{$email}">{$email}</a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					Password reset URL:</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<a href="{$website_reset_password_url}">{$website_reset_password_url}</a></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<div align="center">\r\n	<hr align="center" noshade="noshade" size="1" width="100%" />\r\n</div>\r\n<p>\r\n	Please note this URL is only valid until {$reset_password_expire_date}</p>', 'f sdfdsfg fdg fdg', '<p>\r\n	fsdfsd sd fsdfdgfdg</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	d fgfdg</p>', 'reset_password'),
(2, 'New Account: Password Setup', '<p>Dear Customer, <br />\r\n  You been granted access to the website {$website_name} as {$usergroup}. Below is the  URL where you can set your new password: </p>\r\n<table border="0" cellspacing="1" cellpadding="0">\r\n  <tr>\r\n    <td colspan="3"><br />\r\n      <strong>Account information:</strong></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>Email: </p></td>\r\n    <td><p>&nbsp;</p></td>\r\n    <td><p><a href="mailto:{$email}">{$email}</a></p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>Password setup URL: </p></td>\r\n    <td><p>&nbsp;</p></td>\r\n    <td><p><a href="{$website_reset_password_url}">{$website_reset_password_url}</a></p></td>\r\n  </tr>\r\n</table>\r\n<div align="center">\r\n  <hr size="1" width="100%" noshade="noshade" align="center" />\r\n</div>\r\n<p>Please note this URL is only valid until {$reset_password_expire_date}</p>', '', '', 'new_password_setup'),
